
import util from '../src/index';
import * as chalk from 'chalk';

import * as readline from 'readline';



util.log('11');
util.log('11');
util.log('11');
util.write(chalk.yellow('33333'));
util.write('write输出2');
util.write(chalk.yellow('write输出2'));
util.log('11');
util.write('write输出33');


describe('axiba-util - 测试', () => {


   it('write输出', () => {
       util.log('');
       util.write('write输出1');
       util.write('write输出2');
       util.write('write输出3');

       util.log('');
       util.write('write输出4');
       util.write('write输出5');
   });

   it('log输出', () => {
       util.log('log输出');
   });

   it('warn输出', () => {
       util.warn('warn输出');
   });

   it('error输出', () => {
       util.error('error输出');
   });

   it('success输出', () => {
       util.error('success输出');
   });


   it('生成日记文件', (cb) => {
       util.createLogFile().then(bl => {
           cb();
       })
   });

   it('获取uuid码', () => {
       util.uuid();
   });


   it('清除前后空格', (cb) => {
       if ('哈哈' === util.trim(' 哈哈  ')) {
           cb();
       } else {
           throw '失败';
       }
   });


   it('性能测试', () => {
       util.performanceTest(() => 1);
   });

   it('执行cmd 命令', (cb) => {
       util.exec('echo 执行cmd命令').then(bl => {
           cb();
       })
   });

   it('input输入', (cb) => {
       util.input('输入文字！').then(value => {
           cb();
       });
   });

   it('confirm', (cb) => {
       util.confirm('选择！').then(value => {
           cb();
       });
   });

   it('list选择', (cb) => {
       util.list('列表选择！', ['A', 'B']).then(value => {
           cb();
       });
   });

});


